CREATE DATABASE entregable1;

CREATE TABLE "users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(130) NOT NULL,
  "email" varchar(50) UNIQUE NOT NULL,
  "password" varchar(10) NOT NULL,
  "age" int NOT NULL
);

CREATE TABLE "users_registers" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "userId" int NOT NULL,
  "roleId" int NOT NULL,
  "courseId" int
);

CREATE TABLE "courses" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar(50) NOT NULL,
  "description" varchar(200) NOT NULL,
  "levelId" int NOT NULL,
  "teacherId" int,
  "categoryId" int NOT NULL
);


CREATE TABLE "videos" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar(100) NOT NULL,
  "url" varchar(150) UNIQUE NOT NULL
);

CREATE TABLE "courses_videos" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "videoId" int NOT NULL,
  "courseId" int NOT NULL
);

CREATE TABLE "categories" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(100) NOT NULL
);

CREATE TABLE "levels" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "level" varchar(50) NOT NULL
);

CREATE TABLE "roles" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "role" varchar NOT NULL
);

CREATE TABLE "teacher" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "userId" int NOT NULL
);

ALTER TABLE "teacher" ADD FOREIGN KEY ("userId") REFERENCES "users" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("categoryId") REFERENCES "categories" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("teacherId") REFERENCES "teacher" ("id");

ALTER TABLE "courses_videos" ADD FOREIGN KEY ("courseId") REFERENCES "courses" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("levelId") REFERENCES "levels" ("id");

ALTER TABLE "users_registers" ADD FOREIGN KEY ("userId") REFERENCES "users" ("id");

ALTER TABLE "users_registers" ADD FOREIGN KEY ("roleId") REFERENCES "roles" ("id");

ALTER TABLE "users_registers" ADD FOREIGN KEY ("courseId") REFERENCES "courses" ("id");

ALTER TABLE "courses_videos" ADD FOREIGN KEY ("videoId") REFERENCES "videos" ("id");


